package daytwo.interfaceexample;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0; i <= 10; i++) {
			switch(i) {
			case 0:
				System.out.println("i is " + i);
				break;
			case 1:
				System.out.println("i is " + i);
				break;
			case 2:
				System.out.println("i is " + i);
				break;
			case 3:
				System.out.println("i is " + i);
				break;
			case 4:
				System.out.println("i is " + i);
				break;
			default:
				System.out.println("i is greater than 4");
			}
		}
	}

}
